import React, { Component } from 'react'
import mainService from '../services/main.service';

class StartComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
            name: "player1",
            seed: "",
            size: "",
            isActive: false
        }
        this.changeName = this.changeName.bind(this);
        this.changeSeed = this.changeSeed.bind(this);
        this.changeSize = this.changeSize.bind(this);
        this.startGame = this.startGame.bind(this);
    }

    componentDidMount() {
        // EmployeeService.getEmployeeById(this.state.id).then( res => {
        //     this.setState({employee: res.data});
        // })
    }


    startGame() {
        const { name, seed, size } = this.state;
        const { history } = this.props;

        const realSize = size !== "" ? size : null;


        const params = { seed: seed, size: realSize };
        mainService.startNewGameWithParams(name, params).then((res) => {
            console.log(res.data);
            localStorage.setItem("auth", JSON.stringify(res.data))
            history.push("/game");
        });
    }




    changeName(event) {
        this.setState({ name: event.target.value });
    }

    changeSeed(event) {
        this.setState({ seed: event.target.value });
    }

    changeSize(event) {
        this.setState({ size: event.target.value });
    }

    handleShow = () => {
        this.setState({ isActive: true });
    };

    handleHide = () => {
        this.setState({ isActive: false });
    };

    render() {
        const { name, seed, size } = this.state;
        return (
            <div>
                <div className="m-5" >
                    name:<input value={name} onChange={this.changeName} />
                </div>
                {this.state.isActive ? (<button onClick={this.handleHide}>hide details</button>) : (<button onClick={this.handleShow}>more details</button>)}
                {this.state.isActive && <div id="params" >

                    <div className="m-5" >
                        seed:<input value={seed} onChange={this.changeSeed} />
                    </div>
                    <label>
                        Size:
                        <select value={size} onChange={this.changeSize}>
                            <option value="small">small</option>
                            <option value="medium">medium</option>
                            <option value="big">big</option>
                        </select>
                    </label>
                </div>}

                <div className="m-5" >

                    <button type="button" className="btn btn primary" onClick={this.startGame}>new Game</button>

                </div>
            </div>
        )
    }
}

export default StartComponent
