import React, { Component } from 'react'
import mainService from '../services/main.service';

class GameComponent extends Component {

    constructor(props) {
        super(props)

        this.state = {
            gameId: JSON.parse(localStorage.getItem("auth")).gameId,
            inviteLink: "http://localhost:3000/joingame/" + JSON.parse(localStorage.getItem("auth")).gameId,
            player1: {},
            player2: {},
            width:16,
            height:16,
            board: {}
        }
    }

    componentDidMount() {
        const { gameId } = this.state;
        mainService.getGameData(gameId).then((res) => {
            console.log(res.data);
            this.setState({ player1: res.data.player1, player2: res.data.player2 });
        });
        mainService.getBoard(gameId).then((res) => {
            console.log("board:" ,res.data);
            this.setState({ board:res.data });

        });
    }

    // loadBoard(){
    //     mainService.getBoard(gameId).then((res) => {
    //         console.log(res.data);
    //         this.setState({ board:res.data });
    //         // -> hier eigentlich komplete game
    //     });
    // }



    renderBoard() {
        const {width,height} = this.state;

        return(<table class="chess-board">
             <tbody>
            {[...Array(width).keys()].map((x)=>
            (<tr>
                {[...Array(height).keys()].map((y)=>this.renderField(x,y))}</tr>))}
            </tbody>
            </table>);



    }

    renderField(x,y){
        const {board} = this.state;
        let symbol = "";
        if(board.board){
            if(board.board[x][y]!==null){
                symbol = board.board[x][y].symbol;
            }
        }
        if((x+y) % 2===0){
            return (<td class="white" onClick={()=>this.selectField(x,y)}>{symbol}</td>);
        }
        return (<td class="dark"  onClick={()=>this.selectField(x,y)}>{symbol}</td>);
    }

    selectField(){

    }


    render() {
        const { inviteLink, player1, player2 } = this.state;
        return (
            <div>
                {inviteLink}
                <div></div>
                here is the Game: Player1: {player1 && player1.name} {player2 && <>Player2: {player2.name}</>}
                <div></div>

                <div style={{display: 'flex', justifyContent: 'center'}}>
                <div className="centered">
                    {this.renderBoard()}
                </div>
</div>


            </div>
        )
    }
}

export default GameComponent
