import React, { Component } from 'react'
import mainService from '../services/main.service';
import PieceComponent from './piece.component';

class GameComponent extends Component {

    constructor(props) {
        super(props)

        this.state = {
            gameId: JSON.parse(localStorage.getItem("auth")).gameId,
            inviteLink: "http://localhost:3000/joingame/" + JSON.parse(localStorage.getItem("auth")).gameId,
            player1: {},
            player2: {},
            width:16,
            height:16,
            board: {},
            selectedField:{}
        }
        this.loadGameData();
        this.loadBoard();
    }

    loadGameData() {
        const { gameId } = this.state;
        mainService.getGameData(gameId).then((res) => {
            //console.log(res.data);
            this.setState({ player1: res.data.player1, player2: res.data.player2 });
        });

    }



    loadBoard(){
        const { gameId } = this.state;
        mainService.getBoard(gameId).then((res) => {
            console.log("board:" ,res.data);
            this.setState({ board:res.data });

        });
    }



    renderBoard() {
        const {width,height} = this.state;

        return(<table class="chess-board">
             <tbody>
            {[...Array(width).keys()].map((x)=>
            (<tr>
                {[...Array(height).keys()].map((y)=>this.renderField(x,y))}</tr>))}
            </tbody>
            </table>);



    }

    renderField(x,y){
        const {board,selectedField} = this.state;
        let symbol = "";
        let className = "dark";
        let selected = false;
        if(board.board){
            if(board.board[x][y]!==null){
                symbol = board.board[x][y].symbol;
            }
        }
        if((x+y) % 2===0){
           className="white"
        }
        if(selectedField.x===x && selectedField.y===y){
            selected = true;
        }

        return (<td class={className} onClick={()=>this.selectField(x,y)} onMouseOver={()=>this.mouseOverField(x,y)}><PieceComponent symbol={symbol} selected={selected}></PieceComponent></td>);
    }

    selectField(x,y){
        const {board} =this.state;
        if(board.board[x][y]){
            this.setState({selectedField:{x:x,y:y}});
        }
        //console.log("selected:",x,",",y);


    }

    mouseOverField(x,y){
        //    console.log("mouseOver:",x,",",y);
    }


    render() {
        const { inviteLink, player1, player2 } = this.state;
        return (
            <div>
                {inviteLink}
                <div></div>
                here is the Game: Player1: {player1 && player1.name} {player2 && <>Player2: {player2.name}</>}
                <div></div>

                <div style={{display: 'flex', justifyContent: 'center'}}>
                <div className="centered">
                    {this.renderBoard()}
                </div>
            </div>

            </div>
        )
    }
}

export default GameComponent
