import React, { Component } from 'react'
import mainService from '../services/main.service';
import Canvas from './canvas.component';
import PieceComponent from './piece.component';

class GameComponent extends Component {

    constructor(props) {
        super(props)

        this.state = {
            gameId: JSON.parse(localStorage.getItem("auth")).gameId,
            inviteLink: "http://localhost:3000/joingame/" + JSON.parse(localStorage.getItem("auth")).gameId,
            player1: {},
            player2: {},
            width:16,
            height:16,
            boardData: {}, // data from server
            boardView: [], // symbol, playertype, selected, canMoveTo
            selectedField:{}
        }
        this.loadGameData();
        this.loadBoard();
        this.selectField = this.selectField.bind(this);
    }

    loadGameData() {
        const { gameId } = this.state;
        mainService.getGameData(gameId).then((res) => {
            //console.log(res.data);
            this.setState({ player1: res.data.player1, player2: res.data.player2 });
        });

    }



    loadBoard(){
        const { gameId } = this.state;
        mainService.getBoard(gameId).then((res) => {
            let bv=res.data.board;

            for (let i = 0; i < bv.length; i++) {
                for (let j = 0; j < bv[0].length; j++) {
                    if(res.data.board[i][j]===null){
                        bv[i][j]={selected:false,
                            canMoveTo: false,
                            symbol: "",
                            owner: "",
                            possibleMoves: []};
                    } else {
                        bv[i][j]={selected:false,
                        canMoveTo: false,
                        symbol: bv[i][j].symbol,
                        owner: bv[i][j].owner,
                        possibleMoves: bv[i][j].possibleMoves};
                    }
                }
            }
            console.log(bv);
            this.setState({ boardData:res.data,boardView:bv,width:bv[0].length,height:bv.length});

        });
    }

    renderBoard() {
        const {width,height} = this.state;

        return(<table class="chess-board">
             <tbody>
            {[...Array(height).keys()].map((y)=>
            (<tr>
                {[...Array(width).keys()].map((x)=>this.renderField(x,y))}</tr>))}
            </tbody>
            </table>);



    }

    renderField(x,y){
        const {boardView,selectedField} = this.state;
        let symbol = "";
        let className = "dark";
        let selected = false;
        let canMove = false;

        if((x+y) % 2===0){
           className="white"
        }
        if(selectedField.x===x && selectedField.y===y){
            selected = true;
            className="selected";
        }
        if(JSON.stringify(boardView)!=="[]"){
            symbol = boardView[y][x].symbol;
            if(boardView[y][x].canMoveTo){
                canMove = boardView[y][x].canMoveTo;
                className="canmove";
            }
        }

        return (<td class={className} onClick={()=>this.selectField(x,y)} onMouseOver={()=>this.mouseOverField(x,y)}><PieceComponent symbol={symbol} selected={selected} canMove={canMove}></PieceComponent></td>);
    }

    selectField(x,y){
        const {boardView} =this.state;
        let newBoard=boardView;
        if(boardView[y][x].canMoveTo){
            //
        }
        const possibleMoves =newBoard[y][x].possibleMoves; 
        for (let i = 0; i < boardView.length; i++) {
            for (let j = 0; j < boardView[0].length; j++) {
                newBoard[i][j].canMoveTo=false;
            }
        }

        for(let i=0;i<possibleMoves.length;i++){
            newBoard[possibleMoves[i].y][possibleMoves[i].x].canMoveTo = true;
            console.log("move: ",possibleMoves[i].x, ", ", possibleMoves[i].y);
        }
        this.setState({boardView:newBoard, selectedField:{x:x,y:y}});
        //console.log(boardView);
    }

    removeMoveTos(boardView){

    }

    mouseOverField(x,y){
        //    console.log("mouseOver:",x,",",y);
    }


    clickOnCanvas(event){
        console.log(event);
        console.log(event.clientX-event.target.offSetLeft,",",event.clientY-event.target.offSetTop);
    }


    render() {
        const { inviteLink, player1, player2 ,width,height} = this.state;
        var ctx = null;

        const draw = (ctx, frameCount) => {
            // ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height)
            // ctx.fillStyle = '#000000'

            // ctx.beginPath()
            // ctx.arc(250, 100, 80*Math.sin(frameCount*0.05)**2, 0, 2*Math.PI)
            // ctx.fill()

            const squareSize = 30;
            // position of board's top left
            const boardTopx = 30;
            const boardTopy = 30;

            ctx.canvas.width=squareSize*(width+1)
            ctx.canvas.height=squareSize*(height+1)
            for(let i=0; i<width; i++) {
              for(let j=0; j<height; j++) {
                ctx.fillStyle = ((i+j)%2==0) ? "white":"gray";
                let xOffset = boardTopx + j*squareSize;
                let yOffset = boardTopy + i*squareSize;
                ctx.fillRect(xOffset, yOffset, squareSize, squareSize);
              }
            }
            // draw the border around the chessboard
            ctx.strokeStyle = "black";
            ctx.strokeRect(boardTopx, boardTopy, squareSize*width, squareSize*height)


          }

        return (
            <div>
                {inviteLink}
                <div></div>
                here is the Game: Player1: {player1 && player1.name} {player2 && <>Player2: {player2.name}</>}
                <div></div>

                <div style={{display: 'flex', justifyContent: 'center'}}>
                <div className="centered">
                    {this.renderBoard()}
                </div>
            </div>
            <Canvas draw={draw} onClick={this.clickOnCanvas} />
            </div>
        )
    }
}

export default GameComponent
