import React, { Component } from 'react'
import Canvas from '../game/canvas.component';
import PieceGenerator from '../generator/piece-generator';
import RandomGenerator from '../generator/random-generator';
import UtilFunctions from '../generator/random-generator';

class PiecesComponent extends Component {

    constructor(props) {
        var pg = new PieceGenerator(30, 40, "" + Math.random());
        super(props);
        this.state = {
            width: 30,
            height: 40,
            pieces: []
        }

    }

    drawMethod() {
        const { piece, width, height } = this.state;

        const draw = (ctx, frameCount) => {
            ctx.canvas.width = width
            ctx.canvas.height = height
            ctx.drawImage(piece, 0, 0);
        }
        return draw;
    }

    drawPieces() {
        const { number } = this.state;
        return number.map(() => <Canvas draw={this.drawMethod()} />)
    }

    render() {
        return this.drawPieces();
    }
}

export default PiecesComponent
