import React, { Component } from 'react'

class PieceGeneratorComponent extends Component {

    static seed = 213478;
    static width = 600;
    static height = 600;

    static nextRand() {
        var hi = this.seed / (2147483647 / 48271);
        var lo = this.seed % (2147483647 / 48271);
        var test = 48271 * lo - (2147483647 % 48271) * hi;
        if (test > 0) {
            this.seed = test;
        } else {
            this.seed = test + 2147483647;
        }
        return this.seed * (1.0 / 2147483647);
    }



    static drawPolygon() {

        var pointNumber = this.randNumOfRange(3, 5);
        console.log("pointNumber=" + pointNumber)
        //console.log(Math.floor(this.nextRand() * 16777215).toString(16));
        var color = "#" + Math.floor(this.nextRand() * 16777215).toString(16);

        var pts = "";
        var ptsMirror = "";
        for (var i = 0; i < pointNumber; i++) {
            var x = this.randNumOfRange(0, this.width);
            var y = this.randNumOfRange(0, this.height);
            pts += x + "," + y + " ";
            var x2 = this.width - x;
            ptsMirror += x2 + "," + y + " ";
            //  console.log(i);
        }

        return <><polygon points={pts} fill={color} />
            <polygon points={ptsMirror} fill={color} /></>;
    }


    static drawPiece(width, height, seed, owner) {
        this.seed = seed;
        this.width = width;
        this.height = height;

        return (
            <>
                <svg height={this.height} width={this.width}>
                    {this.drawPolygon()}
                    {this.drawPolygon()}
                    {this.drawPolygon()}
                    {this.drawPolygon()}
                    {this.drawPolygon()}
                </svg>
            </>
        )
    }



    static drawPolygonCanvas(c2) {

        var pointNumber = this.randNumOfRange(3, 10);
        console.log("pointNumber=" + pointNumber)
        var color = "#" + Math.floor(Math.random() * 16777215).toString(16);

        c2.fillStyle = color;
        c2.beginPath();
        for (var i = 0; i < pointNumber; i++) {
            var x = this.randNumOfRange(0, this.width);
            var y = this.randNumOfRange(0, this.height);
            //pts += x + "," + y + " ";

            if (i === 0) {
                c2.moveTo(x, y);
            } else {
                c2.lineTo(x, y);
            }


        }
        c2.closePath();
        c2.fill();

        //   c2.fillStyle = '#f00';
        c2.beginPath();

        for (var j = j; j < pointNumber; j++) {
            var x = this.randNumOfRange(0, this.width);
            var y = this.randNumOfRange(0, this.height);
            var x2 = this.width - x;
            //  ptsMirror += x2 + "," + y + " ";
            if (j === 0) {
                c2.moveTo(x2, y);
            } else {
                c2.lineTo(x2, y);
            }
        }
        c2.closePath();
        c2.fill();
        // c2.beginPath();
        // c2.moveTo(20, 40); // point 1
        // c2.lineTo(70, 50); // point 2
        // c2.lineTo(10, 80); // point 3
        // c2.lineTo(10, 10); // point 4
        // c2.lineTo(10, 70); // point 5
        // c2.closePath();      // go back to point 1
        // c2.fill();         // draw stroke line

        // c2.beginPath();
        // c2.moveTo(120, 140); // point 1
        // c2.lineTo(170, 50); // point 2
        // c2.lineTo(110, 180); // point 3
        // c2.lineTo(180, 70); // point 4
        // c2.lineTo(110, 20); // point 5
        // c2.closePath();      // go back to point 1
        // c2.fill();

        // return <><polygon points={pts} fill={color} />
        //     <polygon points={ptsMirror} fill={color} /></>;
    }

    static randNumOfRange(a, b) {
        // return Math.round(this.nextRand() * (b - a) + a);
        return Math.round(Math.random() * (b - a) + a);
    }


    static drawPieceCanvas(width, height, seed, owner) {
        this.seed = seed;
        this.width = width;
        this.height = height;

        var canvas2 = document.createElement('canvas');
        canvas2.width = width;
        canvas2.height = height;
        var context2 = canvas2.getContext('2d');
        this.drawPolygonCanvas(context2)

        return (
            canvas2
        )
    }




}

export default PieceGeneratorComponent
