class RenderFunctions {
    static renderMatterAnimation(ctx, bodies, frameCount) {


        //TODO: compute forces? i think not
        // opacity down
        // delete when opacity is 0 or below

        for (var i = 0; i < bodies.length; i += 1) {

            var vertices = bodies[i].vertices;

            ctx.beginPath();
            ctx.moveTo(vertices[0].x, vertices[0].y);

            for (var j = 1; j < vertices.length; j += 1) {
                ctx.lineTo(vertices[j].x, vertices[j].y);
            }

            ctx.lineTo(vertices[0].x, vertices[0].y);
            bodies[i].render.opacity -= frameCount * 0.0001;

            ctx.fillStyle = "rgba(100,0,0,", 255 * bodies[i].render.opacity, ")";
            ctx.fill();
        }



    }
}
export default RenderFunctions
