import axios from 'axios';

const MAIN_API_BASE_URL = "http://localhost:8080/api";

class MainService {

    authHeader(){
        const user = JSON.parse(localStorage.getItem("user"));

        if(user && user.accessToken){
            return {Authorization: "Bearer " + user.accessToken};
        }
    }

    startNewGame(name){
        return axios.get(MAIN_API_BASE_URL + "/newgame/" + name)
    }

    joinGame(gameId,name){
        return axios.get(MAIN_API_BASE_URL + "/joingame/" + gameId + "/" + name)
    }

    getGameData(gameId){
        return axios.get(MAIN_API_BASE_URL + "/gamedata/" + gameId,)
    }

    getBoard(gameId){
        return axios.get(MAIN_API_BASE_URL + "/board/" + gameId)
    }

    play(gameId,draw){
        return axios.get(MAIN_API_BASE_URL + "/board/" + gameId,draw)
    }
}

export default new MainService()
