import React, { Component } from 'react'
import Canvas from '../game/canvas.component';
import PieceGenerator from '../generator/piece-generator';

class PieceDataComponent extends Component {

    constructor(props) {

        super(props);
        this.state = {
            pieceId: this.props.match.params.id,
            width: 300,
            height: 400,
            piece: pg.drawPieceCanvas("P1")
        }
        var pg = new PieceGenerator(300, 400, pieceId);
    }

    drawMethod() {
        const { piece, width, height } = this.state;

        const draw = (ctx, frameCount) => {
            ctx.canvas.width = width
            ctx.canvas.height = height
            ctx.drawImage(piece, 0, 0);
        }
        return draw;
    }


    render() {
        const { symbol, selected, canMove } = this.props;
        let style = {};
        let str = symbol
        if ((selected || canMove)) {
            style = { color: 'green' };
