import React, { Component } from 'react'
import RandomGenerator from './random-generator';

class PieceGenerator extends Component {

    constructor(width, height, seedstr) {
        super();
        this.gen = new RandomGenerator(seedstr);
        this.width = width;
        this.height = height;
        this.canvas2 = document.createElement('canvas');
        this.c2 = this.canvas2.getContext('2d');
        this.canvas2.width = width;
        this.canvas2.height = height;
    }



    drawMirroredPolygon() {

        var pointNumber = this.gen.randNumOfRange(3, 10);
        //console.log("pointNumber=" + pointNumber)
        var r = Math.floor(this.gen.random() * 255).toString(16);
        var g = Math.floor(this.gen.random() * 255).toString(16);
        var b = Math.floor(this.gen.random() * 255).toString(16);
        var color = "#" + r + g + b;


        // var color = "#" + Math.floor(this.random() * 255).toString(2);
        // 16 777 216
        console.log(color);

        var points = [];
        for (var i = 0; i < pointNumber; i++) {
            var x = this.gen.randNumOfRange(0, this.width);
            var y = this.gen.randNumOfRange(0, this.height);
            points.push({ x: x, y: y });
        }


        this.c2.fillStyle = color;
        this.c2.fill(this.drawShape(points));
        this.c2.fill(this.drawMirrorShape(points));
    }

    drawMirroredPolygonColorDiff() {

        var pointNumber = this.gen.randNumOfRange(3, 10);
        //console.log("pointNumber=" + pointNumber)
        var r = Math.floor(this.gen.random() * 255).toString(16);
        var g = Math.floor(this.gen.random() * 255).toString(16);
        var b = Math.floor(this.gen.random() * 255).toString(16);
        var color = "#" + r + g + b;


        r = Math.floor(this.gen.random() * 255).toString(16);
        g = Math.floor(this.gen.random() * 255).toString(16);
        b = Math.floor(this.gen.random() * 255).toString(16);
        var color2 = "#" + r + g + b;

        // var color = "#" + Math.floor(this.random() * 255).toString(2);
        // 16 777 216
        console.log(color);

        var points = [];
        for (var i = 0; i < pointNumber; i++) {
            var x = this.gen.randNumOfRange(0, this.width);
            var y = this.gen.randNumOfRange(0, this.height);
            points.push({ x: x, y: y });
        }


        this.c2.fillStyle = color;
        this.c2.fill(this.drawShape(points));
        this.c2.fillStyle = color2;
        this.c2.fill(this.drawMirrorShape(points));
    }


    // just one
    drawPolygon() {

        var pointNumber = this.gen.randNumOfRange(3, 10);
        //console.log("pointNumber=" + pointNumber)
        var r = Math.floor(this.gen.random() * 255).toString(16);
        var g = Math.floor(this.gen.random() * 255).toString(16);
        var b = Math.floor(this.gen.random() * 255).toString(16);
        var color = "#" + r + g + b;


        // var color = "#" + Math.floor(this.random() * 255).toString(2);
        // 16 777 216
        console.log(color);

        var points = [];
        for (var i = 0; i < pointNumber; i++) {
            var x = this.gen.randNumOfRange(0, this.width);
            var y = this.gen.randNumOfRange(0, this.height);
            points.push({ x: x, y: y });
        }


        this.c2.fillStyle = color;
        this.c2.fill(this.drawShape(points));

    }


    drawShape(points) {
        // ??????????????????????????????????????????? new Path2D(d);    // path from SVG path data
        var shape = new Path2D();
        for (var i = 0; i < points.length; i++) {
            if (i === 0) {
                shape.moveTo(points[i].x, points[i].y);
            } else {
                //shape.quadraticCurveTo(points[i - 1].x, points[i - 1].y, points[i].x, points[i].y)
                shape.lineTo(points[i].x, points[i].y);
            }
        }

        return shape;
    }

    drawMirrorShape(points) {
        // ??????????????????????????????????????????? new Path2D(d);    // path from SVG path data
        var shape = new Path2D();
        for (var i = 0; i < points.length; i++) {
            var x = this.width - points[i].x;
            if (i === 0) {
                shape.moveTo(x, points[i].y);
            } else {
                shape.lineTo(x, points[i].y);
            }
        }

        return shape;
    }




    drawPieceCanvas(owner) {

        this.drawMirroredPolygon();
        this.drawMirroredPolygonColorDiff();
        this.drawMirroredPolygon();
        this.drawMirroredPolygon();
        this.drawMirroredPolygonColorDiff();
        this.drawMirroredPolygon();
        this.drawMirroredPolygon();

        return (
            this.canvas2
        );
    }




}

export default PieceGenerator
