import React, { Component } from 'react'

class PieceGeneratorComponent extends Component {








    static drawPolygon() {
        var pointNumber = randNumOfRange(3, 7);
        var color;
        var pts = "";
        for (var i = 0; i < pointNumber; i++) {
            var x = 5;
            var y = 3;
            pts += x + "," + y + " ";
        }

        return <polygon points="20,10 25,80 16,90 " fill="lime" stroke="purple" stroke-width="1" />;
    }

    static randNumOfRange(a, b) {
        return Math.round(Math.random() * (b - a) + a);
    }


    static drawPiece() {
        return (
            <svg height="660" width="660">
                <circle cx="300" cy="300" r="200" stroke="black" stroke-width="3" fill="red" />
                {this.drawPolygon()}
            </svg>
        )
    }



}

export default PieceGeneratorComponent
