import React, { Component } from 'react'

class PieceGeneratorComponent extends Component {


    static loadConf() {
        //Configuration
        return {
            width: 460,
            height: 660
        };
    }

    static xmur3(str) {
        for (var i = 0, h = 1779033703 ^ str.length; i < str.length; i++)
            h = Math.imul(h ^ str.charCodeAt(i), 3432918353),
                h = h << 13 | h >>> 19;

        h = Math.imul(h ^ h >>> 16, 2246822507);
        h = Math.imul(h ^ h >>> 13, 3266489909);
        return (h ^= h >>> 16) >>> 0;

    }



    static drawPolygon() {
        const conf = this.loadConf();

        const width = conf.width;
        const height = conf.height;
        const mirror = width / 2;


        var pointNumber = this.randNumOfRange(3, 6);
        console.log(Math.floor(Math.random() * 16777215).toString(16));
        var color = "#" + Math.floor(Math.random() * 16777215).toString(16);

        var pts = "";
        var ptsMirror = "";
        for (var i = 0; i < pointNumber; i++) {
            var x = this.randNumOfRange(0, width);
            var y = this.randNumOfRange(0, height);
            pts += x + "," + y + " ";
            var x2 = width - x;
            ptsMirror += x2 + "," + y + " ";
        }

        return <><polygon points={pts} fill={color} />
            <polygon points={ptsMirror} fill={color} /></>;
    }

    static randNumOfRange(a, b) {
        return Math.round(Math.random() * (b - a) + a);
    }

    static filter() {
        return <defs>
            <filter id="f1" x="0" y="0">
                <feGaussianBlur in="SourceGraphic" stdDeviation="15" />
            </filter>
        </defs>;
    }


    static drawPiece() {
        const conf = this.loadConf();

        const width = conf.width;
        const height = conf.height;

        return (
            <>
                <svg height={height} width={width}>
                    {this.drawPolygon()}
                    {this.drawPolygon()}
                    {this.drawPolygon()}
                    {this.drawPolygon()}
                    {this.drawPolygon()}
                </svg>
                {this.xmur3("asd")}
            </>
        )
    }



}

export default PieceGeneratorComponent
